CREATE OR ALTER FUNCTIon prefixEmail
(
    @fullname NVARCHAR(MAX)
)
RETURNS VARCHAR(MAX)
as
BEGIN
    DECLARE @text VARCHAR(MAX) = dbo.convertText(@fullname);
    DECLARE @nameParts TABLE (id INT IDENTITY(1,1), Part NVARCHAR(MAX));

    INSERT INTO @nameParts (Part)
    SELECT value
    FROM STRING_SPLIT(@text, ' ');

    DECLARE @res VARCHAR(MAX) = (SELECT Top 1 Part FROM @nameParts order by id desc);

    SELECT @res = @res + SUBSTRING(Part, 1, 1)
    FROM @nameParts
    WHERE Part <> ''
    RETURN lower(substring(@res, 1, len(@res)-1));
END;


